                      Documentation Friedman Paper_March2019
** This SAS code is the statistical analyses for the 2018 study by Mark Friedman and Scott Appel
   Title: Energy expenditure and body composition changes after an isocaloric ketogenic diet in overweight and obese men: a secondary analysis of energy expenditure and physical activity */
** Excel Data from the Hall et al Study can be downloaded: https://osf.io/6srfq/ Hall et al SAS Code can be downloaded there as well;
PROC IMPORT OUT= Chamber
DATAFILE= “Z:\BAC_0541_Friedman\datasets\DataDocumentation.xlsx”
DBMS=xlsx REPLACE; SHEET=“Chamber”; GETNAMES=YES; RUN;
PROC IMPORT OUT=DLW
DATAFILE=“Z:\BAC_0541_Friedman\datasets\DataDocumentation.xlsx”
DBMS= xlsx REPLACE; SHEET=‘DLW’; RUN;
PROC IMPORT OUT=DLW
DATAFILE=“Z:\BAC_0541_Friedman\datasets\DataDocumentation.xlsx”
DBMS= xlsx REPLACE; SHEET=‘DLW’; RUN;
PROC IMPORT OUT=accel
DATAFILE=“Z:\BAC_0541_Friedman\datasets\DataDocumentation.xlsx”
DBMS= xlsx REPLACE; SHEET=“Accelerometer”; RUN;
/* Table 1 */
/* N = 16 EEdlw */
proc mixed data=dlw;
class subject_ID stage;
model TEE_DLWChamber_unadjusted=stage subject_ID / s;
repeated /subject=subject_ID type=CS;
lsmeans stage;
estimate ‘stage 3 vs 2’ stage −1 1;
Ods output lsmeans=lsm;
Ods output tests3= fixed;
where subject_ID not in (‘04-012’);
run;
/* N= 17 EEdlw */
proc mixed data=dlw;
class subject_ID stage;
model TEE_DLWChamber_unadjusted = stage subject_ID / s;
repeated /subject=subject_ID type=CS;
lsmeans stage;
estimate ‘stage 3 vs 2’ stage −1 1;
Ods output lsmeans= lsm;
Ods output tests3= fixed;
/*where subject_ID not in (‘04-012’);*/
run;
data DLW1; set DLW;
TEEDLWnonChamber= TEE_DLWChamber_unadjusted*7/5 - TEE_Chamber_unadjusted*2/5;
run;
/* N = 16 EEnonchamber */
proc mixed data=dlw1;
class subject_ID stage;
model TEEDLWnonChamber = stage subject_ID / s /* influence(iter=10 estimates) TDEE_DLW*/;
repeated /subject=subject_ID type=CS;
lsmeans stage;
estimate ‘stage 3 vs 2’ stage −1 1;
Ods output lsmeans= lsm;
Ods output tests3= fixed;
where subject_ID not in (‘04-012’);
run;
/* N = 17 EEnonchamber */
proc mixed data=dlw1;
class subject_ID stage;
model TEEDLWnonChamber = stage subject_ID / s /* influence(iter=10 estimates) TDEE_DLW*/;
repeated /subject=subject_ID type=CS;
lsmeans stage;
estimate ‘stage 3 vs 2’ stage −1 1;
Ods output lsmeans= lsm;
Ods output tests3= fixed;
run;
/* N = 16 EEchamber */
proc mixed data=dlw1;
class subject_ID stage;
model TEE_Chamber_unadjusted = stage subject_ID / s /* influence(iter=10 estimates) TDEE_DLW*/;
repeated /subject=subject_ID type=CS;
lsmeans stage;
estimate ‘stage 3 vs 2’ stage −1 1;
Ods output lsmeans= lsm;
Ods output tests3= fixed;
where subject_ID not in (‘04-012’);
run;
/* Table 2 -- Accelerometer */
proc means data = accel mean stderr;
   class stage chamber_day location;
   var total_3D_Counts;
   where stage in (2,3) and wear_time min_ ge 720 and last2weeks = 1 and chamber_day in (0,1) and subID ne ‘KEE 04012’; ;
run;
/* mixed models for accelerometer data
Separate models for Hip/Ankle/Wrist
with ‘the outlier’ [04-012] removed */
proc univariate plot data = accel normaltest; class location; var total_3d_counts;
where stage in (2,3) and wear_time min_ ge 720 and last2weeks = 1 and chamber_day in (0,1); run; /* test outcome var for normality */
/* due to non-normality, running mixed model that doesn’t require outcome  variable be normal */
proc glimmix data= accel;
  class stage (ref=“2”) chamber_day (ref=“0”) subID;
  model total_3d_counts = stage chamber_day stage*chamber_day / SOLUTION CL;
  random subID;
  LSMEANS stage*chamber_day;
  where stage in (2,3) and wear_time min_ ge 720 and last2weeks = 1 and chamber_day in (0,1) and location =‘hip’ and subID ne ‘KEE 04012’;
run;
proc glimmix data= accel;
  class stage (ref=“2”) chamber_day (ref=“0”) subID;
  model total_3d_counts = stage chamber_day stage*chamber_day / SOLUTION CL ;
  random subID;
  LSMEANS stage*chamber_day;
  where stage in (2,3) and wear_time min_ ge 720 and last2weeks = 1 and chamber_day in (0,1) and location =‘wrist’ and subID ne ‘KEE 04012’;
run;
proc glimmix data= accel;
  class stage (ref=“2”) chamber_day (ref=“0”) subID;
  model total_3d_counts = stage chamber_day stage*chamber_day / SOLUTION CL ;
  random subID;
  LSMEANS stage*chamber_day;
  where stage in (2,3) and wear_time min_ ge 720 and last2weeks = 1 and chamber_day in (0,1) and location =‘ankle’ and subID ne ‘KEE 04012’;
run;
/* Sleeping energy expenditure */
/* Using data from DLW tab */
data dlw;
set dlw;
hourly_SEE = smr_chamber_unadjusted * 60;
daily_SEE = hourly_SEE * 24;
run;
proc means data = dlw n mean stderr;
class stage;
var smr_cnamber_unadjusted daily_SEE;
**where subject_id ne “04-012”;
run;
/* one sample ttest (on the differences KD-BD) */
proc transpose data = dlw out = flipped_dlw;
by subject_id ;
id stage;
var daily_SEE;
run;
data flipped_dlw;
set flipped_dlw;
diff_SEE =_3 - _2;
run;
proc ttest data = flipped_dlw; var diff_SEE; run;
/* EEDLW and hip accelerometer correlations */
data hip;
set accel;
subID_rebuild = substr(subID,5,2)||”-”||substr(subID,7,3); drop subID;
if location ne ‘hip’ then delete;
run;
data hip2;
set hip;
rename subid_rebuild = subid;
run;
data hip_accel_teedlw;
merge hip2 (in=in1)
      chamber (in=in2);
by subid stage date;
if in2;
run;
proc corr data = hip_accel_teedlw;
var total_3D_counts EE_binned;
where stage = 2;
run;
proc corr data = hip_accel_teedlw;
var total_3D_counts EE_binned;
where stage = 3;
run;
