sec.preformat:
data {
 int N;
 int N_se;
 int N_sp;
 int y;
 int x;
 int z;
}
parameters {
 real<lower=0,upper=1> p;
 real<lower=0,upper=1> se;
 real<lower=0,upper=1> sp;
}
transformed parameters {
 real<lower=0,upper=1> p_obs;
 p_obs = se * p + (1 - sp) * (1 - p);
}
model {
 //priors
 p ∼ beta(1, 1);
 se ∼ beta(1, 1);
 sp ∼ beta(1, 1);
 //likelihood
 y ∼ binomial(N, p_obs);
 x ∼ binomial(N_se, se);
 z ∼ binomial(N_sp, sp);
}
